mapper.select:
SELECT * FROM logging_event
WHERE trace_id = #{traceId}
ORDER BY timestmp

SELECT * FROM logging_slow WHERE 1=1
AND timestmp >= #{startTime}
AND timestmp <= #{endTime}
AND trace_id LIKE CONCAT('%', #{traceId}, '%')
AND user_id LIKE CONCAT('%', #{userId}, '%')
AND ip_address LIKE CONCAT('%', #{ipAddress}, '%')
AND query LIKE CONCAT('%', #{query}, '%')
ORDER BY timestmp DESC LIMIT #{limit} OFFSET #{offset}

SELECT * FROM logging_error WHERE 1=1
AND timestmp >= #{startTime}
AND timestmp <= #{endTime}
AND trace_id LIKE CONCAT('%', #{traceId}, '%')
AND user_id LIKE CONCAT('%', #{userId}, '%')
AND ip_address LIKE CONCAT('%', #{ipAddress}, '%')
AND query LIKE CONCAT('%', #{query}, '%')
ORDER BY timestmp DESC LIMIT #{limit} OFFSET #{offset}
