mapper.select:
SELECT *
FROM fwk_application
WHERE 1=1
AND ID=#{param.id}
AND application_name like concat("%", #{param.application_name}, "%")
AND created_date = #{param.created_date}
AND modified_date = #{param.modified_date}
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM fwk_application
WHERE 1=1
AND ID=#{id}
AND application_name like concat("%", #{application_name}, "%")
AND created_date = #{created_date}
AND modified_date = #{modified_date}

SELECT ID
FROM fwk_application
where application_name = #{application_name}

mapper.update:
UPDATE fwk_application
SET application_name=#{application_name}, modified_date = sysdate()
WHERE ID = #{id}

UPDATE  fwk_application
SET modified_date= sysdate()
WHERE ID=#{id}
